    for (let i = 0; i < count; i++) {
        const x = (Math.random() - 0.5) * 50;
        const y = (Math.random() - 0.5) * 50;
        const zPos = Math.random() * 100;
        positions[i * 3] = x;
        positions[i * 3 + 1] = y;
        positions[i * 3 + 2] = zPos;
        initialZ[i] = zPos;
    }
    return { positions, initialZ };
}

function WarpStarfield({ speed = 2.0, color = "#00ffff" }: { speed?: number, color?: string }) {
    const points = useRef<THREE.Points>(null!);
    const count = 3000;

    const [{ positions }] = useState(() => generateWarpStarfield(count));
    const positionsRef = useRef(positions);

    useFrame((state, delta) => {
        const pos = positionsRef.current;
        // Warp effect: Move stars towards camera
        for (let i = 0; i < count; i++) {
            const i3 = i * 3;
            // Move z
            pos[i3 + 2] += (speed * 10 * delta);

            // Loop back
            if (pos[i3 + 2] > 20) {
                pos[i3 + 2] = -80; // Reset far back
                // Reshuffle x/y for variety
                pos[i3] = (Math.random() - 0.5) * 50;
                pos[i3 + 1] = (Math.random() - 0.5) * 50;
            }
        }
        points.current.geometry.attributes.position.needsUpdate = true;

