  1→import 'package:cybervpn_mobile/features/onboarding/data/repositories/onboarding_repository_impl.dart';
  2→import 'package:flutter_test/flutter_test.dart';
  3→import 'package:mocktail/mocktail.dart';
  4→import 'package:shared_preferences/shared_preferences.dart';
  5→
  6→class MockSharedPreferences extends Mock implements SharedPreferences {}
  7→
  8→void main() {
  9→  late MockSharedPreferences mockPrefs;
 10→  late OnboardingRepositoryImpl repository;
 11→
 12→  setUp(() {
 13→    mockPrefs = MockSharedPreferences();
 14→    repository = OnboardingRepositoryImpl(sharedPreferences: mockPrefs);
 15→  });
 16→
 17→  group('hasCompletedOnboarding', () {
 18→    test('returns false when key does not exist', () async {
 19→      when(() => mockPrefs.getBool('onboarding_complete')).thenReturn(null);
 20→
 21→      final result = await repository.hasCompletedOnboarding();
 22→
 23→      expect(result, isFalse);
 24→      verify(() => mockPrefs.getBool('onboarding_complete')).called(1);
 25→    });
 26→
 27→    test('returns false when key is false', () async {
 28→      when(() => mockPrefs.getBool('onboarding_complete')).thenReturn(false);
 29→
 30→      final result = await repository.hasCompletedOnboarding();
 31→
 32→      expect(result, isFalse);
 33→    });
 34→
 35→    test('returns true when key is true', () async {
 36→      when(() => mockPrefs.getBool('onboarding_complete')).thenReturn(true);
 37→
 38→      final result = await repository.hasCompletedOnboarding();
 39→
 40→      expect(result, isTrue);
 41→    });
 42→  });
 43→
 44→  group('completeOnboarding', () {
 45→    test('writes true to SharedPreferences', () async {
 46→      when(() => mockPrefs.setBool('onboarding_complete', true))
 47→          .thenAnswer((_) async => true);
 48→
 49→      await repository.completeOnboarding();
 50→
 51→      verify(() => mockPrefs.setBool('onboarding_complete', true)).called(1);
 52→    });
 53→
 54→    test('after completion, hasCompletedOnboarding returns true', () async {
 55→      when(() => mockPrefs.setBool('onboarding_complete', true))
 56→          .thenAnswer((_) async => true);
 57→      when(() => mockPrefs.getBool('onboarding_complete')).thenReturn(true);
 58→
 59→      await repository.completeOnboarding();
 60→      final result = await repository.hasCompletedOnboarding();
 61→
 62→      expect(result, isTrue);
 63→    });
 64→  });
 65→
 66→  group('getPages', () {
 67→    test('returns exactly 4 onboarding pages', () async {
 68→      final pages = await repository.getPages();
 69→
 70→      expect(pages, hasLength(4));
 71→    });
 72→
 73→    test('all pages have non-empty title, description, and asset', () async {
 74→      final pages = await repository.getPages();
 75→
 76→      for (final page in pages) {
 77→        expect(page.titleKey, isNotEmpty);
 78→        expect(page.descriptionKey, isNotEmpty);
 79→        expect(page.animationAsset, isNotEmpty);
 80→      }
 81→    });
 82→
 83→    test('only the last page has an actionLabel', () async {
 84→      final pages = await repository.getPages();
 85→
 86→      for (var i = 0; i < pages.length - 1; i++) {
 87→        expect(pages[i].actionLabel, isNull,
 88→            reason: 'Page $i should not have an actionLabel');
