  1→import 'package:flutter/services.dart';
  2→import 'package:flutter_test/flutter_test.dart';
  3→
  4→/// Test helper for mocking platform channels used by VPN and system features.
  5→///
  6→/// Usage:
  7→/// ```dart
  8→/// late PlatformChannelMocks mocks;
  9→///
 10→/// setUp(() {
 11→///   mocks = PlatformChannelMocks();
 12→///   mocks.setUp();
 13→/// });
 14→///
 15→/// tearDown(() => mocks.tearDown());
 16→/// ```
 17→class PlatformChannelMocks {
 18→  final List<MethodCall> _vpnCalls = [];
 19→  final List<MethodCall> _killSwitchCalls = [];
 20→
 21→  /// All method calls received by the VPN platform channel.
 22→  List<MethodCall> get vpnCalls => List.unmodifiable(_vpnCalls);
 23→
 24→  /// All method calls received by the kill switch platform channel.
 25→  List<MethodCall> get killSwitchCalls => List.unmodifiable(_killSwitchCalls);
 26→
 27→  /// Response map for VPN channel methods. Set return values here.
 28→  final Map<String, dynamic> vpnResponses = {};
 29→
 30→  /// Response map for kill switch channel methods.
 31→  final Map<String, dynamic> killSwitchResponses = {};
 32→
 33→  /// Registers mock handlers for all platform channels.
 34→  void setUp() {
 35→    TestDefaultBinaryMessengerBinding.instance.defaultBinaryMessenger
 36→        .setMockMethodCallHandler(
 37→      const MethodChannel('com.cybervpn.mobile/vpn'),
 38→      (call) async {
 39→        _vpnCalls.add(call);
 40→        return vpnResponses[call.method];
 41→      },
 42→    );
 43→
 44→    TestDefaultBinaryMessengerBinding.instance.defaultBinaryMessenger
 45→        .setMockMethodCallHandler(
 46→      const MethodChannel('com.cybervpn.mobile/kill_switch'),
 47→      (call) async {
 48→        _killSwitchCalls.add(call);
 49→        return killSwitchResponses[call.method];
 50→      },
 51→    );
 52→
 53→    TestDefaultBinaryMessengerBinding.instance.defaultBinaryMessenger
 54→        .setMockMethodCallHandler(
 55→      const MethodChannel('com.cybervpn.mobile/split_tunnel'),
 56→      (call) async => null,
 57→    );
 58→  }
 59→
 60→  /// Removes all mock handlers.
 61→  void tearDown() {
 62→    TestDefaultBinaryMessengerBinding.instance.defaultBinaryMessenger
 63→        .setMockMethodCallHandler(
 64→      const MethodChannel('com.cybervpn.mobile/vpn'),
 65→      null,
 66→    );
 67→    TestDefaultBinaryMessengerBinding.instance.defaultBinaryMessenger
 68→        .setMockMethodCallHandler(
 69→      const MethodChannel('com.cybervpn.mobile/kill_switch'),
 70→      null,
 71→    );
 72→    TestDefaultBinaryMessengerBinding.instance.defaultBinaryMessenger
 73→        .setMockMethodCallHandler(
 74→      const MethodChannel('com.cybervpn.mobile/split_tunnel'),
 75→      null,
 76→    );
 77→
 78→    _vpnCalls.clear();
 79→    _killSwitchCalls.clear();
 80→  }
 81→
 82→  /// Clears recorded calls without removing handlers.
 83→  void reset() {
 84→    _vpnCalls.clear();
 85→    _killSwitchCalls.clear();
 86→  }
 87→
 88→  /// Asserts that the VPN channel received a call with the given [method].
 89→  void expectVpnCall(String method) {
 90→    expect(
 91→      _vpnCalls.any((c) => c.method == method),
 92→      isTrue,
 93→      reason: 'Expected VPN channel call "$method" but got: '
 94→          '${_vpnCalls.map((c) => c.method).toList()}',
 95→    );
 96→  }
