  1→import 'dart:convert';
  2→
  3→import 'package:dio/dio.dart';
  4→
  5→import 'package:cybervpn_mobile/core/utils/app_logger.dart';
  6→import 'package:cybervpn_mobile/features/config_import/domain/entities/parsed_config.dart';
  7→import 'package:cybervpn_mobile/features/config_import/domain/parsers/vpn_uri_parser.dart';
  8→import 'package:cybervpn_mobile/features/config_import/domain/usecases/parse_vpn_uri.dart';
  9→import 'package:cybervpn_mobile/features/vpn_profiles/data/models/fetch_result.dart';
 10→import 'package:cybervpn_mobile/features/vpn_profiles/data/models/parsed_server.dart';
 11→import 'package:cybervpn_mobile/features/vpn_profiles/domain/entities/subscription_info.dart';
 12→
 13→/// Fetches a VPN subscription URL and parses the response into
 14→/// structured subscription metadata and a list of VPN server configs.
 15→///
 16→/// Response headers provide subscription metadata:
 17→/// - `profile-title` — display name for the subscription
 18→/// - `subscription-userinfo` — traffic usage in format
 19→///   `upload=X; download=Y; total=Z; expire=T`
 20→/// - `profile-update-interval` — auto-update interval in hours
 21→/// - `support-url` — provider support/help link
 22→/// - `profile-web-page-url` — provider web page
 23→///
 24→/// Response body is typically Base64-encoded, with each decoded line
 25→/// being a V2Ray URI (`vless://`, `vmess://`, `trojan://`, `ss://`).
 26→///
 27→/// Usage:
 28→/// ```dart
 29→/// final fetcher = SubscriptionFetcher(dio: dio);
 30→/// final result = await fetcher.fetch('https://example.com/sub/token');
 31→/// print('Servers: ${result.servers.length}');
 32→/// print('Expires: ${result.info.expiresAt}');
 33→/// ```
 34→class SubscriptionFetcher {
 35→  /// Creates a [SubscriptionFetcher].
 36→  ///
 37→  /// [dio] is the HTTP client used for the subscription request.
 38→  /// [parseVpnUri] is an optional override for the VPN URI parser;
 39→  /// defaults to [ParseVpnUri] with all built-in protocol parsers.
 40→  SubscriptionFetcher({
 41→    required Dio dio,
 42→    ParseVpnUri? parseVpnUri,
 43→  })  : _dio = dio,
 44→        _parseVpnUri = parseVpnUri ?? ParseVpnUri();
 45→
 46→  final Dio _dio;
 47→  final ParseVpnUri _parseVpnUri;
 48→
 49→  /// HTTP request timeout.
 50→  static const Duration _timeout = Duration(seconds: 10);
 51→
 52→  /// User-Agent header sent with subscription requests.
 53→  static const String _userAgent = 'CyberVPN/1.0';
 54→
 55→  /// Fetch and parse a subscription URL.
 56→  ///
 57→  /// Performs an HTTP GET, parses subscription metadata from response
 58→  /// headers, decodes the body (Base64 → line-separated URIs), and
 59→  /// parses each URI using the existing VPN URI parsers.
 60→  ///
 61→  /// Throws [SubscriptionFetcherException] on network or HTTP errors.
 62→  Future<FetchResult> fetch(String url) async {
 63→    final response = await _fetchRaw(url);
 64→    final info = _parseHeaders(response.headers);
 65→    final (servers, errors) = _parseBody(response.data ?? '');
 66→
 67→    AppLogger.info(
 68→      'Subscription fetched',
 69→      category: 'subscription',
 70→      data: {
 71→        'url': _sanitizeUrl(url),
 72→        'serverCount': servers.length,
 73→        'errorCount': errors.length,
 74→        'title': info.title,
 75→      },
 76→    );
 77→
 78→    return FetchResult(
 79→      info: info,
 80→      servers: servers,
 81→      parseErrors: errors,
 82→    );
 83→  }
 84→
 85→  /// Perform the HTTP GET request.
 86→  Future<Response<String>> _fetchRaw(String url) async {
 87→    final uri = Uri.tryParse(url);
 88→    if (uri == null || !uri.hasScheme || !uri.hasAuthority) {
 89→      throw SubscriptionFetcherException(
 90→        url: url,
 91→        message: 'Invalid subscription URL format',
 92→      );
 93→    }
 94→
 95→    try {
 96→      return await _dio.get<String>(
 97→        url,
 98→        options: Options(
 99→          responseType: ResponseType.plain,
100→          receiveTimeout: _timeout,
101→          sendTimeout: _timeout,
102→          headers: {
103→            'User-Agent': _userAgent,
104→            'Accept': '*/*',
105→          },
106→        ),
107→      );
108→    } on DioException catch (e) {
109→      final statusCode = e.response?.statusCode;
110→      final detail = statusCode != null ? 'HTTP $statusCode' : e.type.name;
111→      throw SubscriptionFetcherException(
112→        url: url,
113→        message: 'Failed to fetch subscription: $detail',
114→        cause: e,
115→      );
116→    }
117→  }
118→
119→  /// Extract subscription metadata from response headers.
120→  SubscriptionInfo _parseHeaders(Headers headers) {
121→    final title = _headerValue(headers, 'profile-title');
122→    final userInfo = _headerValue(headers, 'subscription-userinfo');
123→    final updateInterval = _headerValue(headers, 'profile-update-interval');
124→    final supportUrl = _headerValue(headers, 'support-url');
125→
126→    int uploadBytes = 0;
127→    int downloadBytes = 0;
128→    int totalBytes = 0;
129→    DateTime? expiresAt;
130→
131→    if (userInfo != null) {
132→      final parsed = _parseUserInfo(userInfo);
133→      uploadBytes = parsed['upload'] ?? 0;
134→      downloadBytes = parsed['download'] ?? 0;
135→      totalBytes = parsed['total'] ?? 0;
136→      final expireTimestamp = parsed['expire'];
137→      if (expireTimestamp != null && expireTimestamp > 0) {
138→        expiresAt =
139→            DateTime.fromMillisecondsSinceEpoch(expireTimestamp * 1000);
140→      }
141→    }
142→
143→    int updateIntervalMinutes = 60;
144→    if (updateInterval != null) {
145→      final hours = int.tryParse(updateInterval);
146→      if (hours != null && hours > 0) {
147→        updateIntervalMinutes = hours * 60;
148→      }
149→    }
150→
151→    return SubscriptionInfo(
152→      title: title,
153→      uploadBytes: uploadBytes,
154→      downloadBytes: downloadBytes,
155→      totalBytes: totalBytes,
156→      expiresAt: expiresAt,
157→      updateIntervalMinutes: updateIntervalMinutes,
158→      supportUrl: supportUrl,
159→    );
160→  }
161→
162→  /// Parse the `subscription-userinfo` header.
163→  ///
164→  /// Format: `upload=123; download=456; total=789; expire=1234567890`
165→  /// Values are in bytes; expire is a Unix timestamp in seconds.
166→  Map<String, int> _parseUserInfo(String value) {
167→    final result = <String, int>{};
168→    final parts = value.split(';');
169→    for (final part in parts) {
170→      final trimmed = part.trim();
171→      final eqIndex = trimmed.indexOf('=');
172→      if (eqIndex == -1) continue;
173→      final key = trimmed.substring(0, eqIndex).trim().toLowerCase();
174→      final valStr = trimmed.substring(eqIndex + 1).trim();
175→      final parsed = int.tryParse(valStr);
176→      if (parsed != null) {
177→        result[key] = parsed;
178→      }
179→    }
180→    return result;
181→  }
182→
183→  /// Get the first value of a response header (case-insensitive).
184→  String? _headerValue(Headers headers, String name) {
185→    final values = headers[name];
186→    if (values == null || values.isEmpty) return null;
187→    final value = values.first.trim();
188→    return value.isEmpty ? null : value;
189→  }
190→
191→  /// Decode and parse the response body into a list of servers.
192→  ///
193→  /// Tries Base64 decoding first, falls back to plain text if the
194→  /// body already contains VPN URI schemes.
195→  (List<ParsedServer>, List<String>) _parseBody(String body) {
196→    final trimmed = body.trim();
197→    if (trimmed.isEmpty) return (const [], const []);
198→
199→    final decoded = _decodeBody(trimmed);
200→    final lines = decoded.split(RegExp(r'\r?\n'));
201→    final servers = <ParsedServer>[];
202→    final errors = <String>[];
203→
204→    for (var i = 0; i < lines.length; i++) {
205→      final line = lines[i].trim();
206→      if (line.isEmpty) continue;
207→
208→      final result = _parseVpnUri.call(line);
209→      switch (result) {
210→        case ParseSuccess(:final config):
211→          servers.add(ParsedServer(
212→            name: config.remark ??
213→                '${config.protocol}:${config.serverAddress}',
214→            rawUri: line,
215→            protocol: config.protocol,
216→            serverAddress: config.serverAddress,
217→            port: config.port,
218→            configData: _buildConfigData(config),
219→          ));
220→        case ParseFailure(:final message):
221→          errors.add('Line ${i + 1}: $message');
222→          AppLogger.debug(
223→            'Failed to parse subscription line',
224→            category: 'subscription',
225→            data: {'line': i + 1, 'error': message},
226→          );
227→      }
228→    }
229→
230→    return (servers, errors);
231→  }
232→
233→  /// Try to Base64-decode the body; fall back to plain text.
234→  String _decodeBody(String body) {
235→    // If body already looks like VPN URIs, treat as plain text.
236→    if (_looksLikePlainVpnUris(body)) return body;
237→
238→    try {
239→      var normalized = body.replaceAll('-', '+').replaceAll('_', '/');
240→      final remainder = normalized.length % 4;
241→      if (remainder != 0) {
242→        normalized =
243→            normalized.padRight(normalized.length + (4 - remainder), '=');
244→      }
245→      return utf8.decode(base64.decode(normalized));
246→    } on FormatException {
247→      // Not valid Base64 — treat as plain text.
248→      return body;
249→    }
250→  }
251→
252→  /// Check if content starts with a known VPN URI scheme.
253→  bool _looksLikePlainVpnUris(String content) {
254→    final firstLine = content.split('\n').first.trim().toLowerCase();
255→    return ParseVpnUri.supportedSchemes.any(firstLine.startsWith);
256→  }
257→
258→  /// Build a JSON-encodable config data map from a [ParsedConfig].
259→  Map<String, dynamic> _buildConfigData(ParsedConfig config) {
260→    return <String, dynamic>{
261→      'uuid': config.uuid,
262→      if (config.password != null) 'password': config.password,
263→      if (config.transportSettings != null)
264→        'transport': config.transportSettings,
265→      if (config.tlsSettings != null) 'tls': config.tlsSettings,
266→      if (config.additionalParams != null) 'params': config.additionalParams,
267→    };
268→  }
269→
270→  /// Remove query params and fragment from URL for safe logging.
271→  String _sanitizeUrl(String url) {
272→    final uri = Uri.tryParse(url);
273→    if (uri == null) return '***';
274→    return '${uri.scheme}://${uri.host}${uri.path.isEmpty ? '' : '/***'}';
275→  }
276→}
277→
278→/// Exception thrown when fetching or parsing a subscription URL fails.
279→class SubscriptionFetcherException implements Exception {
280→  const SubscriptionFetcherException({
281→    required this.url,
282→    required this.message,
283→    this.cause,
284→  });
285→
286→  /// The subscription URL that was being fetched.
287→  final String url;
288→
289→  /// Human-readable error description.
290→  final String message;
291→
292→  /// The underlying exception, if any.
293→  final Object? cause;
294→
295→  @override
296→  String toString() => 'SubscriptionFetcherException: $message (URL: $url)';
297→}
298→

