  1→import 'package:cybervpn_mobile/core/domain/vpn_protocol.dart';
  2→import 'package:cybervpn_mobile/core/storage/local_storage.dart';
  3→import 'package:cybervpn_mobile/core/types/result.dart';
  4→import 'package:cybervpn_mobile/core/utils/app_logger.dart';
  5→import 'package:cybervpn_mobile/features/config_import/domain/entities/imported_config.dart';
  6→import 'package:cybervpn_mobile/features/config_import/domain/repositories/config_import_repository.dart';
  7→import 'package:cybervpn_mobile/features/vpn_profiles/domain/entities/profile_server.dart';
  8→import 'package:cybervpn_mobile/features/vpn_profiles/domain/repositories/profile_repository.dart';
  9→
 10→/// Service that migrates legacy single-config imported data into the
 11→/// multi-profile system.
 12→///
 13→/// Reads all [ImportedConfig] entries from [ConfigImportRepository],
 14→/// maps them to [ProfileServer] objects, creates a single "CyberVPN"
 15→/// local profile, sets it as active, and marks migration complete via
 16→/// a SharedPreferences flag.
 17→///
 18→/// Idempotent: checks [LocalStorageWrapper.profileMigrationV1CompleteKey]
 19→/// before running. Safe to call multiple times.
 20→class LegacyProfileMigrationService {
 21→  LegacyProfileMigrationService({
 22→    required ConfigImportRepository configImportRepository,
 23→    required ProfileRepository profileRepository,
 24→    required LocalStorageWrapper localStorage,
 25→  })  : _configImportRepo = configImportRepository,
 26→        _profileRepo = profileRepository,
 27→        _localStorage = localStorage;
 28→
 29→  final ConfigImportRepository _configImportRepo;
 30→  final ProfileRepository _profileRepo;
 31→  final LocalStorageWrapper _localStorage;
 32→
 33→  /// Runs the migration. Returns `true` if migration was performed,
 34→  /// `false` if already completed or no data to migrate.
 35→  Future<bool> migrate() async {
 36→    // 1. Check if already migrated.
 37→    final alreadyDone = await _localStorage.getBool(
 38→      LocalStorageWrapper.profileMigrationV1CompleteKey,
 39→    );
 40→    if (alreadyDone == true) {
 41→      AppLogger.debug(
 42→        'Legacy profile migration already complete',
 43→        category: 'migration',
 44→      );
 45→      return false;
 46→    }
 47→
 48→    try {
 49→      // 2. Read legacy imported configs.
 50→      final legacyConfigs = await _configImportRepo.getImportedConfigs();
 51→
 52→      if (legacyConfigs.isEmpty) {
 53→        AppLogger.info(
 54→          'No legacy configs to migrate — marking complete',
 55→          category: 'migration',
 56→        );
 57→        await _markComplete();
 58→        return false;
 59→      }
 60→
 61→      // 3. Check if profiles already exist (someone may have created
 62→      //    profiles before migration ran — e.g. fresh install with new UI).
 63→      final countResult = await _profileRepo.count();
 64→      final existingCount = switch (countResult) {
 65→        Success(:final data) => data,
 66→        Failure() => 0,
 67→      };
 68→
 69→      if (existingCount > 0) {
 70→        AppLogger.info(
 71→          'Profiles already exist ($existingCount) — skipping migration',
 72→          category: 'migration',
 73→        );
 74→        await _markComplete();
 75→        return false;
 76→      }
 77→
 78→      // 4. Map ImportedConfig → ProfileServer.
 79→      final servers = legacyConfigs
 80→          .asMap()
 81→          .entries
 82→          .map((entry) => _mapToProfileServer(entry.value, entry.key))
 83→          .toList();
 84→
 85→      // 5. Create local profile with all servers.
 86→      final result = await _profileRepo.addLocalProfile(
 87→        'CyberVPN',
 88→        servers,
 89→      );
 90→
 91→      switch (result) {
 92→        case Success(:final data):
 93→          // 6. Set as active profile.
 94→          await _profileRepo.setActive(data.id);
 95→          AppLogger.info(
 96→            'Legacy migration complete: created profile "${data.name}" '
 97→            'with ${servers.length} servers',
 98→            category: 'migration',
 99→          );
100→        case Failure(:final failure):
101→          AppLogger.error(
102→            'Legacy migration failed to create profile: ${failure.message}',
103→            category: 'migration',
104→          );
105→          // Don't mark complete — allow retry on next startup.
106→          return false;
107→      }
108→
109→      // 7. Mark migration complete.
110→      await _markComplete();
111→      return true;
112→    } catch (e, st) {
113→      AppLogger.error(
114→        'Legacy profile migration failed',
115→        error: e,
116→        stackTrace: st,
117→        category: 'migration',
118→      );
119→      // Don't mark complete — allow retry on next startup.
120→      return false;
121→    }
122→  }
123→
124→  /// Converts a legacy [ImportedConfig] to a [ProfileServer].
125→  ProfileServer _mapToProfileServer(ImportedConfig config, int index) {
126→    return ProfileServer(
127→      // ID and profileId are overwritten by the repository — use placeholders.
128→      id: 'legacy-${config.id}',
129→      profileId: '',
130→      name: config.name,
131→      serverAddress: config.serverAddress,
132→      port: config.port,
133→      protocol: _parseProtocol(config.protocol),
134→      configData: config.rawUri,
135→      sortOrder: index,
