275→      AppLogger.info(
276→        'Subscription updated',
277→        category: 'profile',
278→        data: {
279→          'profileId': profileId,
280→          'serverCount': fetchResult.servers.length,
281→        },
282→      );
283→
284→      return const Success(null);
285→    } on SubscriptionFetcherException catch (e) {
286→      return Failure(NetworkFailure(message: e.message));
287→    } catch (e) {
