304→  @override
305→  Future<Result<void>> reorder(List<String> profileIds) async {
306→    try {
307→      final orders = <String, int>{};
308→      for (var i = 0; i < profileIds.length; i++) {
309→        orders[profileIds[i]] = i;
310→      }
311→      await _localDs.updateSortOrders(orders);
312→      return const Success(null);
313→    } catch (e) {
314→      return Failure(CacheFailure(message: 'Failed to reorder: $e'));
315→    }
316→  }
