318→  @override
319→  Future<Result<int>> updateAllDueSubscriptions() async {
320→    try {
321→      // This is called from watchAll, so we need a snapshot.
322→      final profiles = await (_localDs.watchAll().first);
323→      final now = DateTime.now();
324→      var updatedCount = 0;
325→
326→      for (final profile in profiles) {
327→        if (profile.type != ProfileType.remote) continue;
328→        if (profile.lastUpdatedAt == null) continue;
329→
330→        final elapsed = now.difference(profile.lastUpdatedAt!);
331→        final interval = Duration(minutes: profile.updateIntervalMinutes);
332→
333→        if (elapsed >= interval) {
334→          final result = await updateSubscription(profile.id);
335→          if (result.isSuccess) updatedCount++;
336→        }
337→      }
338→
339→      return Success(updatedCount);
340→    } catch (e) {
341→      return Failure(
342→        CacheFailure(message: 'Failed to update subscriptions: $e'),
343→      );
344→    }
345→  }
346→
347→  @override
348→  Future<Result<void>> migrateFromLegacy() async {
349→    try {
350→      // Legacy migration is a stub — the integration layer (INT-2)
351→      // will wire this to the actual legacy storage reads.
352→      AppLogger.info(
353→        'Legacy migration: no-op (placeholder for INT-2)',
354→        category: 'profile',
355→      );
356→      return const Success(null);
357→    } catch (e) {
358→      return Failure(
359→        CacheFailure(message: 'Failed to migrate legacy data: $e'),
360→      );
361→    }
362→  }
363→
364→  @override
365→  Future<Result<int>> count() async {
366→    try {
367→      final c = await _localDs.count();
368→      return Success(c);
369→    } catch (e) {
370→      return Failure(CacheFailure(message: 'Failed to count profiles: $e'));
371→    }
372→  }
373→
374→  // ── Helpers ───────────────────────────────────────────────────────
375→
376→  /// Generates a UUID v4 for new entities.
377→  String _generateId() {
378→    final random = Random.secure();
379→    final bytes = List<int>.generate(16, (_) => random.nextInt(256));
380→    bytes[6] = (bytes[6] & 0x0f) | 0x40;
381→    bytes[8] = (bytes[8] & 0x3f) | 0x80;
382→    final hex =
383→        bytes.map((b) => b.toRadixString(16).padLeft(2, '0')).join();
384→    return '${hex.substring(0, 8)}-${hex.substring(8, 12)}-'
385→        '${hex.substring(12, 16)}-${hex.substring(16, 20)}-'
386→        '${hex.substring(20)}';
387→  }
388→
389→  /// Extracts a human-readable hostname from a URL for display.
390→  String _extractHostName(String url) {
391→    final uri = Uri.tryParse(url);
392→    return uri?.host ?? 'Subscription';
393→  }
394→}
